mapper.update:
UPDATE fr_user
SET logintime = #{logintime,jdbcType=TIMESTAMP}
WHERE phone = #{phone,jdbcType=VARCHAR}

UPDATE fr_user
SET passwd = #{passwd,jdbcType=VARCHAR}
WHERE phone = #{phone,jdbcType=VARCHAR}

mapper.select:
SELECT *
FROM fr_user
WHERE status = 0

    AND name LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')


    AND phone = #{phone,jdbcType=VARCHAR}


    AND company LIKE CONCAT('%',#{company,jdbcType=VARCHAR},'%')


    AND infosource = #{infosource,jdbcType=INTEGER}


    AND mark = #{mark,jdbcType=INTEGER}


     AND logintime >= #{logBegin,jdbcType=DATE}
     AND logintime <= #{logEnd,jdbcType=DATE}


     AND advicetime>=#{adviceBegin,jdbcType=DATE}
     AND advicetime <=#{adviceEnd,jdbcType=DATE}

ORDER BY intime DESC
